/**
 * @param {string[]} words
 * @return {number}
 */
var minimizeConcatenatedLength = function(words) {
    // Dynamic programming with bitmask to track used words
    const n = words.length;
    const dp = Array(1 << n).fill(Infinity);
    dp[0] = 0; // Base case: no words used

    // Helper function to calculate the cost of concatenating two words
    const cost = (a, b) => {
        if (a[a.length - 1] === b[0]) return b.length - 1;
        return b.length;
    };

    for (let mask = 0; mask < (1 << n); ++mask) {
        for (let i = 0; i < n; ++i) {
            if ((mask & (1 << i)) === 0) { // If word i is not used
                const newMask = mask | (1 << i);
                for (let j = 0; j < n; ++j) {
                    if (mask & (1 << j)) { // If word j is used
                        dp[newMask] = Math.min(dp[newMask], dp[mask] + cost(words[j], words[i]));
                    }
                }
                if (dp[mask] === 0) dp[newMask] = Math.min(dp[newMask], words[i].length); // If no word is used before word i
            }
        }
    }

    return dp[(1 << n) - 1];
};
