/**
 * @param {string[]} words
 * @return {number}
 */
var minimizeConcatenatedLength = function(words) {
    // Helper function to calculate the extra length added when joining two strings
    function extraLength(a, b) {
        if (a[a.length - 1] === b[0]) {
            return b.length - 1;
        }
        return b.length;
    }

    const n = words.length;
    const dp = Array.from({ length: 1 << n }, () => Infinity);
    const lastCharIndex = Array.from({ length: 26 }, () => -1);

    dp[0] = 0;

    for (let mask = 1; mask < (1 << n); mask++) {
        for (let i = 0; i < n; i++) {
            if ((mask & (1 << i)) === 0) continue; // Skip if i-th word is not in the current combination
            const prevMask = mask ^ (1 << i); // Mask without the i-th word
            if (prevMask === 0) {
                dp[mask] = words[i].length;
                continue;
            }
            for (let j = 0; j < n; j++) {
                if ((prevMask & (1 << j)) === 0) continue; // Skip if j-th word is not in the previous combination
                dp[mask] = Math.min(dp[mask], dp[prevMask] + extraLength(words[j], words[i]));
            }
        }
    }

    return dp[(1 << n) - 1];
};
