class Solution {
    fun minimizeConcatenatedLength(words: Array<String>): Int {
        val n = words.size
        val dp = Array(1 shl n) { Int.MAX_VALUE }
        dp[0] = 0 // Base case: no words chosen

        for (mask in 0 until (1 shl n)) {
            var lastChar = ' '
            var currentLength = 0
            for (i in 0 until n) {
                if (mask and (1 shl i) != 0) {
                    val word = words[i]
                    currentLength += word.length
                    if (lastChar == word.first()) {
                        currentLength-- // Overlap found, reduce length by 1
                    }
                    lastChar = word.last() // Update last character for next iteration
                }
            }

            for (j in 0 until n) {
                if (mask and (1 shl j) == 0) {
                    // If j-th word hasn't been chosen yet
                    val nextMask = mask or (1 shl j)
                    var extraLength = words[j].length
                    if (lastChar == words[j].first()) {
                        extraLength-- // Overlap found, reduce length by 1
                    }
                    dp[nextMask] = minOf(dp[nextMask], dp[mask] + extraLength)
                }
            }
        }

        return dp.last() // Minimum length for all words concatenated
    }
}
