class Solution {
    fun minimizeConcatenatedLength(words: Array<String>): Int {
        fun join(x: String, y: String): Int {
            return if (x.last() == y.first()) x.length + y.length - 1 else x.length + y.length
        }

        val n = words.size
        if (n == 1) return words[0].length

        // dp[mask][i] represents the minimum length of the string formed by concatenating 
        // words represented by the mask, ending with the ith word.
        val dp = Array(1 shl n) { IntArray(n) { Int.MAX_VALUE } }

        // Initialize dp for single word cases
        for (i in 0 until n) dp[1 shl i][i] = words[i].length

        for (mask in 1 until (1 shl n)) {
            for (last in 0 until n) {
                if (mask and (1 shl last) == 0) continue // last is not in the current mask
                val prevMask = mask xor (1 shl last)
                if (prevMask == 0) continue // This should not happen due to initialization

                for (secondLast in 0 until n) {
                    if (prevMask and (1 shl secondLast) == 0) continue // secondLast is not in prevMask
                    val cost = join(words[secondLast], words[last])
                    dp[mask][last] = minOf(dp[mask][last], dp[prevMask][secondLast] + cost - words[last].length)
                }
            }
        }

        // Find the minimum length among all combinations ending with any word
        var answer = Int.MAX_VALUE
        for (i in 0 until n) {
            answer = minOf(answer, dp[(1 shl n) - 1][i])
        }

        return answer
    }
}
