class Solution {

    /**
     * @param String[] $words
     * @return Integer
     */
    function minimizeConcatenatedLength($words) {
        $n = count($words);
        $dp = array_fill(0, 1 << $n, PHP_INT_MAX);
        $dp[0] = 0;
        $lastChar = array_fill(0, 1 << $n, "");
        for ($mask = 0; $mask < (1 << $n); $mask++) {
            for ($i = 0; $i < $n; $i++) {
                if (($mask & (1 << $i)) == 0) {
                    $nextMask = $mask | (1 << $i);
                    $newLastChar = $words[$i][strlen($words[$i]) - 1];
                    $lenToAdd = strlen($words[$i]);
                    if ($lastChar[$mask] == $words[$i][0]) {
                        $lenToAdd--;
                    }
                    if ($dp[$nextMask] > $dp[$mask] + $lenToAdd) {
                        $dp[$nextMask] = $dp[$mask] + $lenToAdd;
                        $lastChar[$nextMask] = $newLastChar;
                    }
                }
            }
        }
        return $dp[(1 << $n) - 1];
    }
}
