class Solution {

    /**
     * @param String[] $words
     * @return Integer
     */
    function minimizeConcatenatedLength($words) {
        $n = count($words);
        $graph = array_fill(0, $n, array_fill(0, $n, 0));
        for ($i = 0; $i < $n; $i++) {
            for ($j = 0; $j < $n; $j++) {
                if ($i !== $j) {
                    $lenI = strlen($words[$i]);
                    $lenJ = strlen($words[$j]);
                    for ($k = min($lenI, $lenJ); $k > 0; $k--) {
                        if (substr($words[$i], -$k) === substr($words[$j], 0, $k)) {
                            $graph[$i][$j] = $k;
                            break;
                        }
                    }
                }
            }
        }

        $dp = array_fill(0, 1 << $n, PHP_INT_MAX);
        $combine = array_fill(0, 1 << $n, array_fill(0, $n, 0));
        for ($i = 0; $i < $n; $i++) {
            $dp[1 << $i] = strlen($words[$i]);
            $combine[1 << $i][$i] = strlen($words[$i]);
        }

        for ($mask = 1; $mask < (1 << $n); $mask++) {
            for ($i = 0; $i < $n; $i++) {
                if ($mask & (1 << $i)) {
                    for ($j = 0; $j < $n; $j++) {
                        if (!($mask & (1 << $j))) {
                            $nextMask = $mask | (1 << $j);
                            $saved = $graph[$i][$j] ?? 0;
                            $newLength = $combine[$mask][$i] + strlen($words[$j]) - $saved;
                            if ($dp[$nextMask] > $newLength) {
                                $dp[$nextMask] = $newLength;
                                for ($k = 0; $k < $n; $k++) {
                                    $combine[$nextMask][$k] = ($k == $j) ? $newLength : $combine[$mask][$k];
                                }
                            }
                        }
                    }
                }
            }
        }

        return $dp[(1 << $n) - 1];
    }
}
