class Solution {

    /**
     * @param String[] $words
     * @return Integer
     */
    function minimizeConcatenatedLength($words) {
        $n = count($words);
        // Initialize dp array. dp[mask] will store the minimum length of the string that can be obtained by using words corresponding to the bits set in mask.
        $dp = array_fill(0, 1 << $n, PHP_INT_MAX);
        $dp[0] = 0; // Base case: no words used, length is 0.

        // Precompute the cost of concatenating any two words with possible reduction.
        $concatCost = [];
        for ($i = 0; $i < $n; $i++) {
            for ($j = 0; $j < $n; $j++) {
                $len = strlen($words[$j]);
                if ($words[$i][strlen($words[$i]) - 1] === $words[$j][0]) {
                    // Reduce the cost by 1 if last char of words[i] equals first char of words[j].
                    $len--;
                }
                $concatCost[$i][$j] = $len;
            }
        }

        // Try all subsets of words.
        for ($mask = 1; $mask < (1 << $n); $mask++) {
            // Try to add each word to the current subset of words.
            for ($j = 0; $j < $n; $j++) {
                if ($mask & (1 << $j)) { // Word j is in the current subset.
                    $prevMask = $mask ^ (1 << $j); // Remove word j to get the previous subset.
                    if ($prevMask === 0) {
                        $dp[$mask] = min($dp[$mask], strlen($words[$j])); // If it's the first word, just take its length.
                    } else {
                        // Try to concatenate word j with all words in the previous subset.
                        for ($i = 0; $i < $n; $i++) {
                            if ($prevMask & (1 << $i)) { // Word i is in the previous subset.
                                // Calculate new length after concatenating word j with word i, considering the reduction.
                                $newLen = $dp[$prevMask] + $concatCost[$i][$j];
                                $dp[$mask] = min($dp[$mask], $newLen);
                            }
                        }
                    }
                }
            }
        }

        // The answer is the minimum length obtained by using all words.
        return $dp[(1 << $n) - 1];
    }
}
