object Solution {
    def minimizeConcatenatedLength(words: Array[String]): Int = {
        val n = words.length
        val dp = Array.fill(1 << n, n)(Int.MaxValue / 2)
        val savings = Array.ofDim[Int](n, n)

        for (i <- 0 until n; j <- 0 until n if i != j) {
            val minLen = Math.min(words(i).length, words(j).length)
            var k = 1
            while (k <= minLen && words(i).endsWith(words(j).substring(0, k))) {
                k += 1
            }
            savings(i)(j) = k - 1
        }

        for (i <- 0 until n) {
            dp(1 << i)(i) = words(i).length
        }

        for (mask <- 0 until (1 << n); i <- 0 until n if (mask & (1 << i)) > 0) {
            for (j <- 0 until n if (mask & (1 << j)) == 0) {
                val nextMask = mask | (1 << j)
                dp(nextMask)(j) = Math.min(dp(nextMask)(j), dp(mask)(i) + words(j).length - savings(i)(j))
            }
        }

        var ans = Int.MaxValue
        for (i <- 0 until n) {
            ans = Math.min(ans, dp((1 << n) - 1)(i))
        }
        ans
    }
}
