class Solution {
    func minimizeConcatenatedLength(_ words: [String]) -> Int {
        var memo = [String: Int]()
        var words = words
        let n = words.count

        func dp(_ index: Int, _ lastWord: String) -> Int {
            if index == n {
                return lastWord.count
            }

            let key = "\(index)-\(lastWord)"
            if let val = memo[key] {
                return val
            }

            let currentWord = words[index]
            var option1 = lastWord + currentWord
            if lastWord.last == currentWord.first {
                option1.removeLast()
            }

            var option2 = currentWord + lastWord
            if currentWord.last == lastWord.first {
                option2.removeLast()
            }

            let result = min(dp(index + 1, option1), dp(index + 1, option2))
            memo[key] = result
            return result
        }

        return dp(1, words[0]) - words[0].count
    }
}
