function minimizeConcatenatedLength(words: string[]): number {
    function join(x: string, y: string): string {
        if (x[x.length - 1] === y[0]) {
            return x + y.substring(1);
        }
        return x + y;
    }

    function dfs(start: number, end: number, memo: Map<string, number>): number {
        const key = `${start}-${end}`;
        if (memo.has(key)) return memo.get(key)!;
        if (start === end) return words[start].length;

        let minLength = Infinity;
        for (let i = start; i < end; i++) {
            const leftPart = dfs(start, i, memo);
            const rightPart = dfs(i + 1, end, memo);
            const joined = join(words[i], words[i + 1]);
            const currentLength = leftPart + rightPart + joined.length - words[i].length - words[i + 1].length;
            minLength = Math.min(minLength, currentLength);
        }

        memo.set(key, minLength);
        return minLength;
    }

    let memo = new Map<string, number>();
    return dfs(0, words.length - 1, memo);
}
