function minimizeConcatenatedLength(words: string[]): number {
    // Helper function to calculate the cost of joining two words
    const cost = (a: string, b: string) => a.endsWith(b[0]) ? b.length - 1 : b.length;

    // Dynamic programming table
    const dp = Array.from({ length: words.length }, () => Array(words.length).fill(Infinity));

    // Base case: cost of single words
    for (let i = 0; i < words.length; i++) dp[i][i] = words[i].length;

    // Fill DP table
    for (let len = 2; len <= words.length; len++) {
        for (let i = 0; i <= words.length - len; i++) {
            const j = i + len - 1;
            for (let k = i; k < j; k++) {
                const joinCost = dp[i][k] + cost(words[k], words[j]) - words[k].length;
                dp[i][j] = Math.min(dp[i][j], joinCost);
            }
        }
    }

    // Reconstruct the minimum length
    let minLen = Infinity;
    for (let i = 0; i < words.length; i++) {
        for (let j = i; j < words.length; j++) {
            minLen = Math.min(minLen, dp[i][j]);
        }
    }

    return minLen;
}
