class Solution:
    def distributeCandies(self, n: int, limit: int) -> int:
        def count_ways(n, limit):
            if n < 0:
                return 0
            if n == 0:
                return 1
            total = 0
            for i in range(limit + 1):
                total += count_ways(n - i, i)
            return total

        return count_ways(n, limit) - count_ways(n, limit - 1)
