impl Solution {
    pub fn divide_array(nums: Vec<i32>, k: i32) -> Vec<Vec<i32>> {
        let mut nums = nums;
        nums.sort_unstable();
        let mut result = Vec::new();
        let mut temp = Vec::new();
        for &num in &nums {
            if temp.len() < 3 {
                if temp.is_empty() || num - temp[0] <= k {
                    temp.push(num);
                } else {
                    temp.clear();
                    temp.push(num);
                }
            }
            if temp.len() == 3 {
                result.push(temp.clone());
                temp.clear();
            }
        }
        if temp.len() == 3 {
            result.push(temp);
        }
        if result.iter().flatten().count() != nums.len() {
            return vec![];
        }
        result
    }
}
