class Solution {
public:
    vector<int> beautifulIndices(string s, string a, string b, int k) {
        vector<int> result;
        vector<int> aIndices, bIndices;

        // Find all occurrences of a and b in s
        for (int i = 0; i <= (int)s.length() - (int)a.length(); ++i) {
            if (s.substr(i, a.length()) == a) aIndices.push_back(i);
        }
        for (int i = 0; i <= (int)s.length() - (int)b.length(); ++i) {
            if (s.substr(i, b.length()) == b) bIndices.push_back(i);
        }

        // Check each index of a for the condition with b
        for (int i : aIndices) {
            bool beautiful = false;
            for (int j : bIndices) {
                if (abs(i - j) <= k) {
                    beautiful = true;
                    break;
                }
            }
            if (beautiful) result.push_back(i);
        }

        return result;
    }
};
