class Solution {
    fun beautifulIndices(s: String, a: String, b: String, k: Int): List<Int> {
        val beautifulIndices = mutableListOf<Int>()
        val aIndices = s.windowed(a.length) { it == a }.mapIndexedNotNull { index, match -> if (match) index else null }
        val bIndices = s.windowed(b.length) { it == b }.mapIndexedNotNull { index, match -> if (match) index else null }

        for (i in aIndices) {
            if (bIndices.any { j -> kotlin.math.abs(i - j) <= k }) {
                beautifulIndices.add(i)
            }
        }

        return beautifulIndices.sorted()
    }
}
