class Solution {
    func beautifulIndices(_ s: String, _ a: String, _ b: String, _ k: Int) -> [Int] {
        let sArray = Array(s)
        let sLength = s.count
        var result = [Int]()

        // Function to check if a substring starting from index 'start' matches the given pattern 'pattern'
        func matchesAt(_ start: Int, _ pattern: String) -> Bool {
            let patternArray = Array(pattern)
            for i in 0..<patternArray.count {
                if start + i >= sLength || sArray[start + i] != patternArray[i] {
                    return false
                }
            }
            return true
        }

        // Find all occurrences of 'a' and 'b' in 's'
        var aIndices = [Int]()
        var bIndices = [Int]()
        for i in 0..<(sLength) {
            if i + a.count <= sLength && matchesAt(i, a) {
                aIndices.append(i)
            }
            if i + b.count <= sLength && matchesAt(i, b) {
                bIndices.append(i)
            }
        }

        // For each occurrence of 'a', find if there's an occurrence of 'b' within 'k' distance
        for aIndex in aIndices {
            var found = false
            for bIndex in bIndices {
                if abs(aIndex - bIndex) <= k {
                    found = true
                    break
                }
            }
            if found {
                result.append(aIndex)
            }
        }

        return result.sorted()
    }
}
