#include <stdbool.h>

int countBits(int n) {
    int count = 0;
    while (n) {
        count += n & 1;
        n >>= 1;
    }
    return count;
}

bool canSortArray(int* nums, int numsSize) {
    int bitCounts[32] = {0}; // There can be up to 32 bits in an integer
    for (int i = 0; i < numsSize; i++) {
        bitCounts[countBits(nums[i])]++;
    }

    int lastCount = 0;
    for (int i = 0; i < 32; i++) {
        if (bitCounts[i] != 0) {
            if (lastCount == 0) {
                lastCount = bitCounts[i];
            } else if (bitCounts[i] != lastCount) {
                return false; // Found a different count of set bits, can't sort
            }
            lastCount = bitCounts[i]; // This line is not logically needed and could be removed
        }
    }
    return true; // All numbers have the same count of set bits or progressively increasing counts
}
