#include <stdbool.h>

int countSetBits(int n) {
    int count = 0;
    while (n) {
        count += n & 1;
        n >>= 1;
    }
    return count;
}

bool canSortArray(int* nums, int numsSize) {
    int freq[32] = {0}; // To store frequency of bit counts, considering maximum 32 bits for an int
    for (int i = 0; i < numsSize; ++i) {
        int setBits = countSetBits(nums[i]);
        freq[setBits]++;
    }

    // Check if all nums can be grouped by their set bit counts
    for (int i = 0; i < numsSize - 1; ++i) {
        int currentBits = countSetBits(nums[i]);
        int nextBits = countSetBits(nums[i + 1]);
        if (freq[currentBits] > 0 && freq[nextBits] > 0 && currentBits != nextBits) {
            // If both groups exist and are different, check for the continuous sequence of set bits
            if (freq[currentBits] != freq[nextBits]) {
                return false; // Can't sort if different groups have unequal frequencies
            }
        }
    }

    return true; // Possible to sort as all required conditions are met
}
