object Solution {
    def findIndices(nums: Array[Int], indexDifference: Int, valueDifference: Int): Array[Int] = {
        val n = nums.length
        val sortedIndices = nums.indices.sortBy(nums)
        for (i <- 0 until n) {
            var j = i + 1
            while (j < n && sortedIndices(j) - sortedIndices(i) < indexDifference) j += 1
            if (j < n && Math.abs(nums(sortedIndices(i)) - nums(sortedIndices(j))) >= valueDifference) {
                return Array(sortedIndices(i), sortedIndices(j))
            }
        }
        Array(-1, -1)
    }
}
