public class Solution {
    public int MaximumLength(string s) {
        int maxLength = -1;
        for (char c = 'a'; c <= 'z'; c++) {
            int count = 0;
            int maxLenForChar = 0;
            for (int i = 0; i < s.Length; i++) {
                if (s[i] == c) {
                    count++;
                    int len = 0;
                    while (i < s.Length && s[i] == c) {
                        i++;
                        len++;
                    }
                    maxLenForChar = Math.Max(maxLenForChar, len);
                    i--; // because the for-loop will increment i again
                }
            }
            if (count >= 3) {
                maxLength = Math.Max(maxLength, maxLenForChar);
            }
        }
        return maxLength;
    }
}
