public class Solution {
    public int MaximumLength(string s) {
        int result = -1;
        for (int i = 0; i < s.Length; i++) {
            for (int j = i + 1; j < s.Length; j++) {
                if (s[i] == s[j]) {
                    int len = j - i;
                    int count = 2;
                    for (int k = j + 1; k <= s.Length - len; k++) {
                        bool match = true;
                        for (int l = 0; l < len; l++) {
                            if (s[i + l] != s[k + l]) {
                                match = false;
                                break;
                            }
                        }
                        if (match) count++;
                    }
                    if (count >= 3) result = Math.Max(result, len);
                }
            }
        }
        return result;
    }
}
