class Solution {
public:
    int maximumLength(string s) {
        int maxLen = -1;
        for (char c = 'a'; c <= 'z'; ++c) {
            int count = 0;
            int currentMax = 0;
            for (int i = 0; i < s.size(); ++i) {
                if (s[i] == c) {
                    ++count;
                    int len = 0;
                    while (i < s.size() && s[i] == c) {
                        ++len;
                        ++i;
                    }
                    currentMax = max(currentMax, len);
                    --i; // Adjust for the increment in the for-loop.
                }
            }
            if (count >= 3) maxLen = max(maxLen, currentMax);
        }
        return maxLen;
    }
};
