int maximumLength(char* s) {
    int maxLen = -1;
    for (int i = 0; s[i]; ++i) {
        for (int len = 1; s[i + len - 1]; ++len) {
            int count = 0;
            for (int j = i; s[j]; ++j) {
                if (s[j] == s[i] && strncmp(s + i, s + j, len) == 0) {
                    ++count;
                    j += len - 1; // Skip over this substring
                }
                if (count == 3) {
                    maxLen = maxLen < len ? len : maxLen;
                    break;
                }
            }
            if (count < 3) break; // If less than 3 occurrences, no longer substring will match 3 times
        }
    }
    return maxLen - 1; // Adjust for the extra increment
}
