int maximumLength(char* s) {
    int maxLen = -1;
    for (int i = 0; s[i]; ++i) {
        for (int j = i + 1; s[j]; ++j) {
            if (s[i] == s[j]) {
                int len = j - i;
                int count = 1;
                for (int k = j; s[k] && s[k] == s[i]; k += len) {
                    if (strncmp(s + i, s + k, len) == 0) {
                        ++count;
                    } else {
                        break;
                    }
                    if (count == 3) {
                        maxLen = len > maxLen ? len : maxLen;
                        break;
                    }
                }
            }
        }
    }
    return maxLen;
}
