func maximumLength(s string) int {
    maxLen := -1
    for i := 0; i < len(s); i++ {
        for j := i + 1; j <= len(s); j++ {
            substr := s[i:j]
            if len(substr) > 0 && allCharsSame(substr) {
                count := 0
                for k := 0; k <= len(s)-len(substr); k++ {
                    if allCharsSame(s[k:k+len(substr)]) && s[i] == s[k] {
                        count++
                    }
                }
                if count >= 3 {
                    maxLen = max(maxLen, len(substr))
                }
            }
        }
    }
    return maxLen
}

func allCharsSame(s string) bool {
    for i := 1; i < len(s); i++ {
        if s[i] != s[0] {
            return false
        }
    }
    return true
}

func max(a, b int) int {
    if a > b {
        return a
    }
    return b
}
