class Solution {
    public int maximumLength(String s) {
        int maxLen = -1;
        for (char c = 'a'; c <= 'z'; c++) {
            int count = 0;
            int maxCurrentCharLen = 0;
            int currentCharLen = 0;
            for (int i = 0; i < s.length(); i++) {
                if (s.charAt(i) == c) {
                    currentCharLen++;
                    maxCurrentCharLen = Math.max(maxCurrentCharLen, currentCharLen);
                } else {
                    if (currentCharLen > 0) {
                        count++;
                        currentCharLen = 0;
                    }
                }
            }
            if (currentCharLen > 0) {
                count++;
            }
            if (count >= 3) {
                maxLen = Math.max(maxLen, maxCurrentCharLen);
            }
        }
        return maxLen;
    }
}
