class Solution {
    public int maximumLength(String s) {
        int result = -1;
        for (int i = 0; i < s.length(); i++) {
            for (int j = i + 1; j < s.length(); j++) {
                if (s.charAt(i) == s.charAt(j)) {
                    int len = 2;
                    while (j + len - 1 < s.length() && s.charAt(i) == s.charAt(j + len - 1)) {
                        len++;
                    }
                    int count = 0;
                    for (int k = 0; k <= s.length() - len + 1; k++) {
                        boolean match = true;
                        for (int l = 0; l < len - 1; l++) {
                            if (s.charAt(k + l) != s.charAt(i)) {
                                match = false;
                                break;
                            }
                        }
                        if (match) count++;
                    }
                    if (count >= 3) {
                        result = Math.max(result, len - 1);
                    }
                }
            }
        }
        return result;
    }
}
