/**
 * @param {string} s
 * @return {number}
 */
var maximumLength = function(s) {
    let maxLen = -1;
    for (let i = 0; i < s.length; i++) {
        for (let j = i + 1; j <= s.length; j++) {
            const substr = s.slice(i, j);
            if (new Set(substr.split('')).size === 1) {
                let count = 0;
                for (let k = 0; k <= s.length - substr.length; k++) {
                    if (s.slice(k, k + substr.length) === substr) {
                        count++;
                    }
                }
                if (count >= 3) {
                    maxLen = Math.max(maxLen, substr.length);
                }
            }
        }
    }
    return maxLen === -1 ? -1 : maxLen - 1;
};
