class Solution {
    fun maximumLength(s: String): Int {
        val counts = IntArray(26)
        s.forEach { counts[it - 'a']++ }

        var maxLength = -1
        for (i in counts.indices) {
            if (counts[i] >= 3) {
                val char = 'a' + i
                var tempLength = 0
                var maxTempLength = 0
                s.forEach { c ->
                    if (c == char) {
                        tempLength++
                        maxTempLength = maxOf(maxTempLength, tempLength)
                    } else {
                        tempLength = 0
                    }
                }
                maxLength = maxOf(maxLength, maxTempLength)
            }
        }
        return if (maxLength >= 3) maxLength else -1
    }
}
