class Solution {
    fun maximumLength(s: String): Int {
        var maxLen = -1
        for (ch in 'a'..'z') {
            var count = 0
            var consecutive = 0
            for (i in s.indices) {
                if (s[i] == ch) {
                    consecutive++
                    if (i == s.length - 1 || s[i + 1] != ch) {
                        if (consecutive >= 3) count++
                        consecutive = 0
                    }
                }
            }
            if (count >= 3) {
                for (len in 3..s.length) {
                    var found = 0
                    for (i in 0..s.length - len) {
                        if (s.substring(i, i + len).all { it == ch }) {
                            found++
                            if (found == 3) {
                                maxLen = maxOf(maxLen, len)
                                break
                            }
                        }
                    }
                    if (found < 3) break
                }
            }
        }
        return maxLen
    }
}
