impl Solution {
    pub fn maximum_length(s: String) -> i32 {
        let mut max_len = -1;
        for c in b'a'..=b'z' {
            let mut count = 0;
            let mut curr_len = 0;
            for &b in s.as_bytes() {
                if b == c {
                    curr_len += 1;
                } else {
                    if curr_len >= 3 {
                        count += 1;
                    }
                    curr_len = 0;
                }
            }
            if curr_len >= 3 {
                count += 1;
            }
            if count >= 3 {
                max_len = max_len.max(curr_len);
            }
        }
        max_len
    }
}
