impl Solution {
    pub fn maximum_length(s: String) -> i32 {
        let mut max_len = -1;
        for c in b'a'..=b'z' {
            let mut lengths = vec![];
            let mut curr_len = 0;
            for &b in s.as_bytes() {
                if b == c {
                    curr_len += 1;
                } else {
                    if curr_len > 0 {
                        lengths.push(curr_len);
                    }
                    curr_len = 0;
                }
            }
            if curr_len > 0 {
                lengths.push(curr_len);
            }
            lengths.sort_unstable();
            let len = lengths.len();
            if len >= 3 {
                max_len = max_len.max(lengths[len - 3]);
            }
        }
        max_len
    }
}
