class Solution {
    func maximumLength(_ s: String) -> Int {
        var maxLength = -1
        let characters = Array(s)
        for char in Set(characters) {
            let filtered = characters.filter { $0 == char }
            if filtered.count >= 3 {
                maxLength = max(maxLength, 1)
                for length in 2...filtered.count where filtered.count >= 3 {
                    let substring = String(repeating: char, count: length)
                    let regex = try! NSRegularExpression(pattern: substring)
                    let nsRange = NSRange(s.startIndex..<s.endIndex, in: s)
                    let matchCount = regex.matches(in: s, options: [], range: nsRange).count
                    if matchCount >= 3 {
                        maxLength = max(maxLength, length)
                    } else {
                        break
                    }
                }
            }
        }
        return maxLength
    }
}
