public class Solution {
    public int MaximumLength(string s) {
        int maxLength = -1;
        for (char ch = 'a'; ch <= 'z'; ++ch) {
            int start = -1, secondStart = -1, count = 0;
            for (int i = 0; i < s.Length; ++i) {
                if (s[i] == ch) {
                    if (start == -1) start = i;
                    count++;
                } else {
                    if (start != -1 && secondStart == -1) secondStart = i;
                    if (i - start >= 3 && start != -1) {
                        maxLength = Math.Max(maxLength, secondStart - start - 1);
                    }
                    if (secondStart != -1 && count >= 3) {
                        start = secondStart;
                        secondStart = -1;
                        count = i - start;
                        i--; // reprocess this character as the start of new sequence
                    } else {
                        start = -1;
                        secondStart = -1;
                        count = 0;
                    }
                }
            }
            if (count >= 3) {
                maxLength = Math.Max(maxLength, s.Length - start - 1);
            }
        }
        return maxLength;
    }
}
