public class Solution {
    public int MaximumLength(string s) {
        int maxLen = -1;
        for (char c = 'a'; c <= 'z'; c++) {
            int freq = 0, first = -1, second = -1;
            for (int i = 0; i < s.Length; i++) {
                if (s[i] == c) {
                    freq++;
                    if (first == -1) first = i;
                    second = i;
                } else {
                    if (freq >= 3) {
                        maxLen = Math.Max(maxLen, second - first + 1);
                    }
                    freq = 0;
                    first = -1;
                }
            }
            if (freq >= 3) {
                maxLen = Math.Max(maxLen, second - first + 1);
            }
        }
        for (int len = maxLen; len > 0; len--) {
            for (char c = 'a'; c <= 'z'; c++) {
                int count = 0;
                for (int i = 0; i <= s.Length - len; i++) {
                    bool valid = true;
                    for (int j = i; j < i + len; j++) {
                        if (s[j] != c) {
                            valid = false;
                            break;
                        }
                    }
                    if (valid) count++;
                    if (count == 3) return len;
                }
            }
        }
        return -1;
    }
}
