class Solution {
public:
    int maximumLength(string s) {
        int maxLen = -1;
        for (char c = 'a'; c <= 'z'; ++c) {
            int count = 0;
            int currentMax = 0;
            for (int i = 0, j = 0; j < s.size(); ++j) {
                if (s[j] == c) {
                    ++count;
                    if (count >= 3) {
                        currentMax = max(currentMax, j - i + 1);
                        while (i < j && count >= 3) {
                            if (s[i] == c) --count;
                            ++i;
                            if (count >= 3) currentMax = max(currentMax, j - i + 1);
                        }
                    }
                } else {
                    i = j + 1; // Move i to the next character after a non-matching character
                    count = 0; // Reset count for the next sequence
                }
            }
            maxLen = max(maxLen, currentMax);
        }
        return maxLen > 2 ? maxLen : -1;
    }
};
