class Solution {
public:
    int maximumLength(string s) {
        vector<int> freq(26, 0);
        int n = s.length(), maxLen = -1;

        for (int len = 1; len <= n; ++len) {
            fill(freq.begin(), freq.end(), 0);
            for (int i = 0; i < len; ++i) {
                freq[s[i] - 'a']++;
            }
            for (int i = 0; i < 26; ++i) {
                if (freq[i] == len) maxLen = max(maxLen, len);
            }

            for (int i = len; i < n; ++i) {
                freq[s[i] - 'a']++;
                freq[s[i - len] - 'a']--;
                for (int j = 0; j < 26; ++j) {
                    if (freq[j] == len) maxLen = max(maxLen, len);
                }
            }
        }

        for (int count = maxLen; count > 0; --count) {
            int occurrences = 0;
            fill(freq.begin(), freq.end(), 0);
            for (int i = 0; i < count; ++i) {
                freq[s[i] - 'a']++;
            }
            for (int i = 0; i < 26; ++i) {
                if (freq[i] == count) occurrences++;
            }

            for (int i = count; i < n; ++i) {
                freq[s[i] - 'a']++;
                freq[s[i - count] - 'a']--;
                for (int j = 0; j < 26; ++j) {
                    if (freq[j] == count) occurrences++;
                }
                if (occurrences >= 3) return count;
            }
        }

        return -1;
    }
};
