/**
 * @param {string} s
 * @return {number}
 */
var maximumLength = function(s) {
    let result = -1;
    for (let char of new Set(s)) {
        const regex = new RegExp(`${char}+`, 'g');
        let matches = s.match(regex) || [];
        let lengths = matches.map(match => match.length).filter(length => length >= 3);
        if (lengths.length > 0) {
            let localMax = Math.max(...lengths);
            for (let len = localMax; len >= 1; len--) {
                let count = lengths.filter(length => length >= len).length;
                if (count >= 3) {
                    result = Math.max(result, len);
                    break;
                }
            }
        }
    }
    return result;
};
