class Solution {
    fun maximumLength(s: String): Int {
        val freq = IntArray(26) { 0 }
        var maxLen = -1
        s.forEach { c -> freq[c - 'a']++ }
        for (i in freq.indices) {
            if (freq[i] >= 3) {
                val char = 'a' + i
                var tempLen = 0
                var count = 0
                for (j in s.indices) {
                    if (s[j] == char) {
                        tempLen++
                        count = 1
                        while (j + count < s.length && s[j + count] == char) {
                            tempLen++
                            count++
                        }
                        maxLen = maxOf(maxLen, tempLen)
                        tempLen = 0
                    }
                }
            }
        }
        return if (maxLen >= 3) maxLen else -1
    }
}
