impl Solution {
    pub fn maximum_length(s: String) -> i32 {
        let mut max_length = 0;
        let mut counts = [0; 26];
        let bytes = s.as_bytes();

        for &b in bytes.iter() {
            let index = (b - b'a') as usize;
            counts[index] += 1;
        }

        for i in 0..26 {
            if counts[i] >= 3 {
                let mut current_length = 0;
                for &b in bytes.iter() {
                    if (b - b'a') as usize == i {
                        current_length += 1;
                        max_length = max_length.max(current_length);
                    } else {
                        current_length = 0;
                    }
                }
            }
        }

        if max_length >= 3 {
            max_length
        } else {
            -1
        }
    }
}
