class Solution {
    func maximumLength(_ s: String) -> Int {
        var result = -1
        let sArray = Array(s)
        var count = [Int](repeating: 0, count: 26)

        for i in 0..<sArray.count {
            let index = Int(sArray[i].asciiValue! - Character("a").asciiValue!)
            count[index] += 1
        }

        for i in 0..<26 {
            if count[i] >= 3 {
                var maxLength = 0
                var tempLength = 0
                for j in 0..<sArray.count {
                    if Int(sArray[j].asciiValue! - Character("a").asciiValue!) == i {
                        tempLength += 1
                        maxLength = max(maxLength, tempLength)
                    } else {
                        tempLength = 0
                    }
                }
                for length in stride(from: maxLength, through: 1, by: -1) {
                    var freq = 0
                    var tempFreq = 0
                    for j in 0..<sArray.count {
                        if Int(sArray[j].asciiValue! - Character("a").asciiValue!) == i {
                            tempFreq += 1
                            if tempFreq == length {
                                freq += 1
                                tempFreq = 0
                            }
                        } else {
                            tempFreq = 0
                        }
                    }
                    if freq >= 3 {
                        result = max(result, length)
                        break
                    }
                }
            }
        }

        return result
    }
}
