class Solution {
    func maximumLength(_ s: String) -> Int {
        var lastChar = Character(" ")
        var currentLength = 0
        var maxPossibleLength = 0
        var charOccurrences = [Character: [Int]]() // Character and lengths of its occurrences

        for char in s {
            if char == lastChar {
                currentLength += 1
            } else {
                if currentLength > 0 {
                    charOccurrences[lastChar, default: []].append(currentLength)
                    maxPossibleLength = max(maxPossibleLength, currentLength)
                }
                lastChar = char
                currentLength = 1
            }
        }
        charOccurrences[lastChar, default: []].append(currentLength) // Add last character occurrences
        maxPossibleLength = max(maxPossibleLength, currentLength)

        var maxLength = -1
        for (_, lengths) in charOccurrences {
            let sortedLengths = lengths.sorted()
            if sortedLengths.count >= 3 {
                for i in sortedLengths.indices.reversed() where sortedLengths[i] >= 3 {
                    if sortedLengths[i] > maxPossibleLength {
                        break // No need to check further as we cannot get a longer special substring
                    }
                    let count = sortedLengths.filter { $0 >= sortedLengths[i] }.count
                    if count >= 3 {
                        maxLength = max(maxLength, sortedLengths[i])
                        break // Found the longest possible for this character
                    }
                }
            }
        }

        return maxLength
    }
}
