function maximumLength(s: string): number {
    let result = -1;
    for (let i = 0; i < s.length; ) {
        let start = i;
        while (i < s.length && s[i] === s[start]) {
            i++;
        }
        const length = i - start;
        if (length >= 3) {
            result = Math.max(result, length);
        }
    }

    if (result === -1) return -1;
    for (let len = result; len >= 1; len--) {
        let count = 0;
        for (let i = 0; i <= s.length - len; i++) {
            if (s.substring(i, i + len) === s[i].repeat(len)) {
                count++;
                if (count === 3) return len;
                i += len - 1; // Skip over this special substring
            } else {
                count = 0;
            }
        }
    }

    return -1;
};
