from typing import List

class Solution:
    def longestEqualSubarray(self, nums: List[int], k: int) -> int:
        max_length = 0
        for num in set(nums):
            left = 0
            right = 0
            deletions = 0
            while right < len(nums):
                if nums[right] != num:
                    if deletions < k:
                        deletions += 1
                    else:
                        while nums[left] == num:
                            left += 1
                        left += 1
                        continue
                max_length = max(max_length, right - left + 1)
                right += 1
        return max_length
