object Solution {
    def longestSemiRepetitiveSubstring(s: String): Int = {
        var maxLen = 0
        for (i <- s.indices) {
            var repeat = false
            var len = 1
            for (j <- i + 1 until s.length) {
                if (s(j) == s(j - 1)) {
                    if (repeat) {
                        len = j - i
                        maxLen = maxLen max len
                        len = 0
                        break
                    }
                    repeat = true
                }
                len += 1
            }
            maxLen = maxLen max len
        }
        maxLen
    }
}
