public class Solution {
    public int MinimumPossibleSum(int n, int target) {
        long sum = 0;
        int mod = 1000000007;
        if (target % 2 == 0) {
            int half = target / 2;
            if (n > half) {
                sum = (long)(half - 1) * half / 2;
                int remaining = n - half;
                sum += (long)half * remaining;
                long start = half + 1;
                sum += (long)(start + start + remaining - 1) * remaining / 2;
            } else {
                sum = (long)(1 + n) * n / 2;
            }
        } else {
            if (n >= target) {
                sum = (long)target * (n - target);
                sum += (long)(1 + target - 1) * (target - 1) / 2;
            } else {
                sum = (long)(1 + n) * n / 2;
            }
        }
        return (int)(sum % mod);
    }
}
