public class Solution {
    public int MinimumPossibleSum(int n, int target) {
        const int MOD = 1000000007;
        long sum = 0;
        if (target % 2 == 0) {
            // If target is even and n is greater than half of target, avoid the number equal to target / 2
            if (n >= target / 2) {
                sum = 1L * (n + 1) * n / 2 - (target / 2);
                sum += n;
            } else {
                sum = 1L * (n + 1) * n / 2;
            }
        } else {
            // If target is odd, we can safely include all numbers as no two will sum to target
            sum = 1L * (n + 1) * n / 2;
        }
        return (int)(sum % MOD);
    }
}
