class Solution {
public:
    int minimumPossibleSum(int n, int target) {
        long long mod = 1e9 + 7;
        long long sum = 0;
        if (target % 2 == 0) {
            // Exclude the number exactly half of target if n is large enough
            long long m = min(n, (target / 2) - 1);
            sum = m * (m + 1); // Sum of first m natural numbers
            if (n > target / 2 - 1) {
                sum += ((n - (target / 2 - 1)) * (target / 2)) + (n - (target / 2));
            }
        } else {
            sum = (1LL * n * (n + 1)) / 2; // Sum of first n natural numbers
        }
        return sum % mod;
    }
};
