class Solution {
    public int minimumPossibleSum(int n, int target) {
        long mod = 1000000007;
        long sum = 0;
        if (target % 2 == 0) {
            int half = target / 2;
            if (n <= half) {
                sum = ((1L + n) * n) / 2;
            } else {
                sum = ((1L + (half - 1)) * (half - 1)) / 2;
                int remaining = n - (half - 1);
                sum += (long) half * remaining + ((1L + remaining - 1) * (remaining - 1)) / 2;
            }
        } else {
            sum = ((1L + n) * n) / 2;
        }
        return (int)(sum % mod);
    }
}
