/**
 * @param {number} n
 * @return {number}
 */
var punishmentNumber = function(n) {
    function isValid(i, square) {
        const str = square.toString();
        for (let start = 1; start <= str.length; start++) {
            for (let end = start; end <= str.length; end++) {
                let sum = 0;
                let pos = 0;
                for (let j = start; j <= end; j *= 10) {
                    const part = Math.floor((square % (j * 10)) / j) * j;
                    sum += part / j;
                    pos += 1;
                    if (sum > i) break;
                }
                if (sum === i) return true;
            }
        }
        return false;
    }

    let result = 0;
    for (let i = 1; i <= n; i++) {
        const square = i * i;
        if (isValid(i, square)) result += square;
    }
    return result;
};
